import React from 'react';
import { connect } from 'react-redux';
import { setText } from '../actions';
class Todoform extends React.Component {

  	onClick(e){
    	const newTodo = this.state.inputValue;

    	this.props.onSaveTodo(newTodo);

    	this.setState({
      		inputValue: ''
    	});
  	}

	render() {
		const {text, setText} = this.props;
		return (
			<React.Fragment>
				<input 
           value={text}
            onChange={e => setText(e.target.value)} />

        <button onClick={e => this.onClick(e)}>Salvar</button>

			</React.Fragment>
		);
	}
}

function mapStateToProps(state) {
  return {
    text: state.text
  };
}

const mapDispatchProps = {setText};

export default connect(mapStateToProps, mapDispatchProps)(Todoform)
